import React, {useState} from 'react'
import "./Card.css";
import { Link } from 'react-router-dom';

function Card( {id, src, title, description} ){

  const handleClick=(id)=>{
    <Link to="/room/id"></Link >
  }

  return (
    <div className='card'>
        <img src={src} alt=''/>
        <div className='card_info'>
            <h2>{title}</h2>
            <h4>{description}</h4>
        </div>
        <div className='card_button'>
            <button onClick={handleClick}>See Rooms</button>
        </div>
    </div>
  )
}

export default Card
