// components/HotelDetails.jsx
import React from 'react';
import hotelDataset from '../data/hotelDataset.json';

const HotelDetails = ({ hotelId }) => {
  // Find the hotel in the dataset based on the provided hotelId
  const hotel = hotelDataset.hotels.find((hotel) => hotel.hotelId === hotelId);

  if (!hotel) {
    // Hotel not found,display an error message/handle it
    return <div>Hotel not found</div>;
  }

  // Render the hotel details
  return (
    <div>
      <h2>{hotel.name}</h2>
      <p>Location: {hotel.location}</p>
      <h3>Rooms:</h3>
      {hotel.rooms.map((room) => (
        <div key={room.roomId}>
          <p>Room Type: {room.roomType}</p>
          <p>Price: ${room.price}</p>
          <p>Availability: {room.availability}</p>
          {/* Render add-ons here */}
        </div>
      ))}
    </div>
  );
};

export default HotelDetails;
