// pages/Home.jsx
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import hotelDataset from '../data/hotelDataset.json';

const Home = () => {
  const navigate = useNavigate();

  // Click handler for hotel card
  const handleHotelCardClick = (hotelId) => {
    navigate(`/hotels/${hotelId}`);
  };

  return (
    <div className='home_section'>
      <h1>Hotel List</h1>
      {hotelDataset.hotels.map((hotel) => (
        <div className='hotel' key={hotel.hotelId}>
            <img src={hotel.image} alt='' style={{}}/>
            <div className='hotel_info'>
                <h2>{hotel.name}</h2>
                <h4>Location: {hotel.location}</h4>
            </div>
          
          {/* Render other hotel information here */}
          <div className='hotel_button'>
            <button onClick={() => handleHotelCardClick(hotel.hotelId)}>
                View Rooms
            </button>
          </div>
          
        </div>
      ))}
    </div>
  );
};

export default Home;
