// components/HotelDetails.jsx
import React from 'react';
import { useParams } from 'react-router-dom'; // Import useParams

import hotelDataset from '../data/hotelDataset.json';

const HotelDetails = () => {
  const { hotelId } = useParams(); // Use useParams to get the hotelId from the URL

  // Find the hotel in the dataset based on the provided hotelId
  const hotel = hotelDataset.hotels.find((hotel) => hotel.hotelId === parseInt(hotelId, 10));

  if (!hotel) {
    // Hotel not found, you can display an error message or handle it as needed
    return <div>Hotel not found</div>;
  }

  // Render the hotel details
  return (
    <div>
      <h2>{hotel.name}</h2>
      <p>Location: {hotel.location}</p>
      <h3>Rooms:</h3>
      {hotel.rooms.map((room) => (
        <div key={room.roomId}>
          <p>Room Type: {room.roomType}</p>
          <p>Price: ${room.price}</p>
          <p>Availability: {room.availability}</p>
          {/* Render add-ons here */}
        </div>
      ))}
    </div>
  );
};

export default HotelDetails;
