// pages/Home.jsx
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import hotelDataset from '../data/hotelDataset.json';

const Home = () => {
  const navigate = useNavigate();

  // Click handler for hotel card
  const handleHotelCardClick = (hotelId) => {
    navigate(`/hotels/${hotelId}`);
  };

  return (
    <div>
      <h1>Hotel List</h1>
      {hotelDataset.hotels.map((hotel) => (
        <div key={hotel.hotelId}>
          <h3>{hotel.name}</h3>
          <p>Location: {hotel.location}</p>
          {/* Render other hotel information here */}
          <button onClick={() => handleHotelCardClick(hotel.hotelId)}>
            View Rooms
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
